import React, { useEffect, useState } from "react";
import { FcCalendar, FcClock } from "react-icons/fc";
import Spinner from "./Spinner";
require("dotenv").config();

function Campaign(props) {
  const contentful = require("contentful");
  const [isLoading, setLoading] = useState(true);

  const client = contentful.createClient({
    space: process.env.REACT_APP_SPACE,
    accessToken: process.env.REACT_APP_ACCESSTOKEN,
  });

  const [campaigns, setCampaign] = useState([]);
  const dateToTime = (dates) =>
    dates.toLocaleString("en-GB", {
      hour12: true,
      hour: "2-digit",
      minute: "numeric",
      timeZone: "Asia/Rangoon",
    });
  const FullDate = (dates) =>
    dates.toLocaleString("en-GB", {
      dateStyle: "long",
    });
  const Card = (props) => {
    return (
      <div className="card-wrapper grid md:grid-cols-2 p-4 rounded-md dark:hover:bg-gray-900">
        <div className="img mx-auto px-2">
          <img src={props.artwork} className="w-60 h-auto" />
        </div>
        <div className="mb-2 text-center md:text-left">
          <div className="text-xl font-bold text-red-400 mt-4 hidden">
            {props.title}
          </div>
          <div className="slogan text-xl text-red-400 font-bold leading-9">
            {props.slogan}
          </div>
          <div className="dateTime tracking-wider">
            <span className="date font-extralight">
              <FcCalendar className="inline-block" />
              {props.date} |{" "}
            </span>
            <span className="time font-extralight">
              <FcClock className="inline-block" />
              {props.time}
            </span>
          </div>
          <div className="city"></div>
          <div className="desc">{props.desc}</div>
        </div>
      </div>
    );
      };;;

  useEffect(() => {
    client
      .getEntries({
        content_type: "campaign",
        order: "-sys.createdAt",
      })
      .then((response) => {
        setCampaign(response.items);
        console.log(response.items);
        setLoading(false);
      });
  }, []);

  return (
    <div className="grid grid-cols-3">
      <div className="featured col-span-2">
        {isLoading ? <Spinner /> : ""}
        {campaigns.slice(0, 1).map((obj) => {
          const localDate = new Date(obj.fields.dateTime);
          const date = FullDate(localDate);
          const time = dateToTime(localDate);
          return (
            <Card
              key={obj.sys.id}
              artwork={obj.fields.artwork.fields.file.url}
              title={obj.fields.title}
              date={date}
              time={time}
              slogan={obj.fields.slogan}
              desc={obj.fields.desc}
            />
          );
        })}
      </div>
      <div className="upcoming">
        <div className="wrapper">
          {campaigns.slice(1).map((obj) => {
            const localDate = new Date(obj.fields.dateTime);
            const date = FullDate(localDate);
            const time = dateToTime(localDate);
            return (
              <Card
                key={obj.sys.id}
                artwork={obj.fields.artwork.fields.file.url}
                title={obj.fields.title}
                date={date}
                time={time}
                slogan={obj.fields.slogan}
                desc={obj.fields.desc}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Campaign;
