import React, { useEffect, useState } from "react";
require("dotenv").config();

function Campaign(props) {
  const contentful = require("contentful");
  const [isLoading, setLoading] = useState(true);

  const client = contentful.createClient({
    space: process.env.REACT_APP_SPACE,
    accessToken: process.env.REACT_APP_ACCESSTOKEN,
  });

  const [campaigns, setCampaign] = useState([]);
  const dateToTime = (dates) =>
    dates.toLocaleString("en-GB", {
      hour12: false,
      hour: "2-digit",
      minute: "numeric",
    });
  const FullDate = (dates) =>
    dates.toLocaleString("en-GB", {
      dateStyle: "long",
    });
  const Card = (props) => {
    return (
          <div className="card-wrapper">
            <div className="img"><img src={props.artwork}/></div>
            <div className="text">
              <div className="title">{props.title}</div>
              <div className="date">{props.dateTime}</div>
              <div className="city"></div>
              <div className="slogan"></div>
              <div className="desc"></div>
            </div>
          </div >
    )
}

  useEffect(() => {
    client
      .getEntries({
        content_type: "campaign",
        order: "-sys.createdAt",
      })
      .then((response) => {
        setCampaign(response.items);
        console.log(response.items);
        setLoading(false);
      });
  }, []);

  return (
    <div>
      {campaigns.map((obj) => {
        const localDate = new Date(obj.fields.dateTime);
        const date = FullDate(localDate);
        return (
          <Card
            key={obj.sys.id}
            artwork={obj.fields.artwork.fields.file.url}
            title={obj.fields.title}
            dateTime={date}
          />
        );
      })}
    </div>
  );
}

export default Campaign;
