import React, { useEffect } from "react";
import { withRouter } from "react-router-dom";

function ScrollToTop(props) {
  useEffect(() => {
    const unlisten = props.listen(() => {
      window.scrollTo(0, 0);
    });
    return () => {
      unlisten();
    };
  }, []);

  return null;
}

export default withRouter(ScrollToTop);
