import React from "react";

function Newsletter() {
  const Newsletter = (props) => {
    return (
      <div className="inline-block mr-4">
        <div className="image">
          <img src={props.image} alt="molotov cover image" className="w-48" />
        </div>
        <div className="text">
          <h3>{props.title}</h3>
          <span>{props.number}</span>
        </div>
        <div className="link">
          <a href={props.link}>Read</a>
        </div>
      </div>
    );
  };
  return (
    <div className="w-full md:max-w-3xl mx-auto items-center justify-center px-2">
      <Newsletter
        image="/images/v1n1.jpeg"
        title="Molotov"
        number="Vol-1 No-1"
        link="/molotov/v1n1"
      />
      <Newsletter
        image="/images/v1n2.jpeg"
        title="Molotov"
        number="Vol-1 No-2"
        link="/molotov/"
      />
      <Newsletter
        image="/images/v1n1.jpeg"
        title="Molotov"
        number="Vol-1 No-1"
        link="/molotov/v1n1"
      />
    </div>
  );
}

export default Newsletter;
