import React, { useState } from 'react';

function Disclaimer(props) {
    const [isOpen, setOpen] = useState(true);

    return (
    <>
        {isOpen? <div className="bg-yellow-400 px-6 py-2 rounded-md">
          <small className="inline-block">
            အချက်အလက်များကို ပြင်ပနေရာအများစုမှ ရယူထားခြင်း ဖြစ်သဖြင့်
            မှားယွင်းမှုများ ရှိပါက
            <a
              href="mailto:contact@010221.org"
              className="cursor-pointer text-red-800"
            >
              contact@010221.org
            </a>
            သို့ ဆက်သွယ်အကြောင်းကြားနိုင်ပါသည်။
          </small>
            <span className="inline-block text-black float-right" onClick={()=>setOpen(false)}>x</span>
        </div>:null}
        </>
    );
}

export default Disclaimer;
