import React, { useEffect, useState } from "react";
import axios from "axios";
import Popup from "reactjs-popup";
import Spinner from "./Parts/Spinner";
import { Link } from "react-router-dom";

function PublicShame(props) {
  const [shame, setShame] = useState([]);
  const [isLoading, setLoading] = useState(true);

  const MyList = (props) => {
    return (
      <Link to={`/shaming/${props.link}`} className="text-gray-500">
        <div
          key={props._id}
          className="innerWrapper px-4 py-2 w-full border-b border-gray-300 last:border-0 flex justify-items-start items-center "
        >
          <div className="thumbnail flex-none mr-4">
            <img
              src={`/images/${
                props.gender === "male"
                  ? "sample-male.png"
                  : "sample-female.png"
              }`}
              alt="sample"
              className="w-24 h-auto inline-block"
            />
          </div>
          <div className="info flex-auto flex-grow justify-self-start">
            <div className="name">
              <h3 className="font-semibold text-gray-700">{props.name}</h3>
            </div>
            <div className="info">
              <span className="text-gray-500 mr-2 pr-2 border-r border-gray-500">
                {props.job}
              </span>
              <span className="text-gray-500">{props.work}</span>
            </div>
          </div>
          <div className="action flex-none justify-self-end">
            <div className="view">
              <button className="px-6 py-2 border border-gray-400 rounded-md">
                <Link to={`/shaming/${props.link}`} className="text-gray-500">
                  View &gt;
                </Link>
              </button>
            </div>
          </div>
        </div>
      </Link>
    );
  };;

  function fetchShaming() {
    axios
      .get("https://mm010221.herokuapp.com/shame/")
      .then((res) => {
        setShame(res.data);
        setLoading(false);
        console.log(res.data)
      })
      .catch((err) => {
        console.log(err);
      });
  }

  useEffect(() => {
    fetchShaming();
  }, []);
  return (
    <div className="w-full items-center justify-center px-1">
      <h1 className="text-xl md:text-3xl font-black text-gray-600 text-center my-4 tracking-wider">
        Public Shaming List
      </h1>
      <hr />
      {isLoading ? <Spinner /> : ""}
      <div
        className={`wrapper w-full md:w-3/5 mx-auto py-2 my-4 border border-gray-300 rounded-md ${
          isLoading ? "hidden" : ""
        }`}
      >
        {shame.map((obj) => {
          return (
            <MyList
              key={obj._id}
              gender={obj.gender}
              name={obj.name}
              job={obj.designation}
              work={obj.department}
              link={obj._id}
            />
          );
        })}
      </div>
    </div>
  );
}

export default PublicShame;
