import React, { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import Tabletop from "tabletop";

function JuntaList(props) {
  const [data, setData] = useState([]);

  const columns = [
    {
      name: "Product",
      selector: "Product",
      sortable: true,
      width: "50%",
    },
    {
      name: "Industry",
      selector: "Industry",
      sortable: true,
      width: "50%",
    },
  ];

  useEffect(() => {
    Tabletop.init({
      key: "1l8JYfCSKMGYGjZ45XhGSmtwS9p8h82sZY9UAP1PaTL4",
      callback: (googleData) => {
        console.log("google sheet data --->", googleData);
        setData(googleData);
      },
      simpleSheet: true,
    });
  }, []);
  return (
    <div className="flex items-center justify-center">
      <div className="tailwindTable">
        <table className="min-w-full bg-white">
          <thead className="bg-gray-800 text-white">
            <tr className="bg-teal-400 flex flex-initial flex-no wrap sm:table-row rounded-l-lg sm:rounded-none mb-2 sm:mb-0">
              <th className="w-1/3 text-left py-3 px-4 uppercase font-semibold text-sm">
                ID
              </th>
              <th className="w-1/3 text-left py-3 px-4 uppercase font-semibold text-sm">
                Product
              </th>
              <th className="w-1/3 text-left py-3 px-4 uppercase font-semibold text-sm">
                Industry
              </th>
            </tr>
          </thead>
          <tbody className="flex-1">
            {data.map((obj) => {
              let id = parseInt(obj.ID);
              return (
                <tr
                  key={obj.ID}
                  className={`${
                    id / 2 === 1 ? "bg-gray-400" : "bg-red-400"
                  } flex flex-initial flex-no wrap sm:table-row rounded-l-lg sm:rounded-none mb-2 sm:mb-0`}
                >
                  <td className="w-1/3 text-left py-3 px-4 text-sm">
                    {obj.ID}
                  </td>
                  <td className="w-1/3 text-left py-3 px-4 text-sm">
                    {obj.Product}
                  </td>
                  <td className="w-1/3 text-left py-3 px-4 text-sm">
                    {obj.Industry}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      {/* <div className="inner w-full">
        <DataTable
          title="Stop Buying Junta Businesses"
          columns={columns}
          data={data}
          striped
          highlightOnHover
        />
      </div> */}
    </div>
  );
}

export default JuntaList;
