import React, { useEffect, useState } from "react";
require("dotenv").config();

function Campaign(props) {
  const contentful = require("contentful");
  const [isLoading, setLoading] = useState(true);

  const client = contentful.createClient({
    space: process.env.REACT_APP_SPACE,
    accessToken: process.env.REACT_APP_ACCESSTOKEN,
  });

  const [campaigns, setCampaign] = useState([]);
  const dateToTime = (dates) =>
    dates.toLocaleString("en-GB", {
      hour12: true,
      hour: "2-digit",
      minute: "numeric",
      timeZone: "Asia/Rangoon",
    });
  const FullDate = (dates) =>
    dates.toLocaleString("en-GB", {
      dateStyle: "long",
    });
  const Card = (props) => {
    return (
      <div className="card-wrapper grid grid-cols-2 border p-4 dark:border-red-500">
        <div className="img w-60 h-auto mx-auto">
          <img src={props.artwork} />
        </div>
        <div className="text-center mb-2">
          <div className="text-xl font-bold text-red-400 mt-4 hidden">
            {props.title}
          </div>
          <div className="slogan text-xl text-red-400 font-bold">
            {props.slogan}
          </div>
          <span className="date font-extralight">{props.date} | </span>
          <span className="time font-extralight">{props.time}</span>
          <div className="city"></div>
          <div className="desc">{props.desc}</div>
        </div>
      </div>
    );
}

  useEffect(() => {
    client
      .getEntries({
        content_type: "campaign",
        order: "-sys.createdAt",
      })
      .then((response) => {
        setCampaign(response.items);
        console.log(response.items);
        setLoading(false);
      });
  }, []);

  return (
    <div>
      {campaigns.map((obj) => {
        const localDate = new Date(obj.fields.dateTime);
        const date = FullDate(localDate);
        const time = dateToTime(localDate);
        return (
          <Card
            key={obj.sys.id}
            artwork={obj.fields.artwork.fields.file.url}
            title={obj.fields.title}
            date={date}
            time={time}
            slogan={obj.fields.slogan}
            desc = {obj.fields.desc}
          />
        );
      })}
    </div>
  );
}

export default Campaign;
