import React, { useEffect, useState } from "react";
import { IoIosArrowBack } from "react-icons/io";
import { Link, useParams } from "react-router-dom";

function CampaignDetail(props) {
  const contentful = require("contentful");
  const [campaign, setCampaign] = useState([]);
  const [loading, setLoading] = useState(true);
  const { id } = useParams();
  const client = contentful.createClient({
    space: process.env.REACT_APP_SPACE,
    accessToken: process.env.REACT_APP_ACCESSTOKEN,
  });

  useEffect(() => {
    client
      .getEntries({
        content_type: "campaign",
        "fields.slug": id,
        order: "sys.createdAt",
      })
      .then((response) => {
        setCampaign(response.items);
        setLoading(false);
      });
  }, []);

  return (
    <div className="w-full md:max-w-4xl mx-auto items-center justify-center px-2">
      <div className="heading flex justify-items-center">
        <Link to="/" className="text-center flex-none my-4">
          <IoIosArrowBack className="text-gray-500 inline-block" size={30} />
          <span>Back</span>
        </Link>
      </div>
      {campaign.map((obj) => {
        return (
          <div className="wrapper mx-auto">
            <div className="image">
              <img
                src={obj.fields.artwork.fields.file.url}
                alt=""
                className="w-full"
              />
            </div>
            <div className="text text-center font-bold text-2xl text-red-400">
              {obj.fields.title}
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default CampaignDetail;
