import axios from "axios";
import React, { useState } from "react";
import { IoIosArrowBack } from "react-icons/io";
import { useHistory, useParams } from "react-router-dom";
import Spinner from "./Parts/Spinner";

function CDMDetail(props) {
  const { id } = useParams();
  let history = useHistory();
  const [profile, setProfile] = useState([]);
  const [isLoading, setLoading] = useState(true);
  axios
    .get("https://mm010221.herokuapp.com/shame/" + id)
    .then((res) => {
      setProfile(res.data);
      setLoading(false);
      console.log(res);
    })
    .catch((err) => {
      console.log(err);
    });
  return (
    <div className="w-full items-center justify-center px-1">
      <div className="heading flex justify-items-center">
        <button onClick={history.goBack} className="items-center flex-none">
          <IoIosArrowBack className="text-gray-500 inline-block" size={30} />
          <span>{id}</span>
        </button>
        <h1 className="text-xl md:text-3xl font-black text-gray-600 text-center my-4 tracking-wider flex-1">
          Public Shaming List
        </h1>
      </div>
      <hr />
      {isLoading ? <Spinner /> : ""}
      <div
        className={`wrapper w-full md:w-3/5 mx-auto py-2 my-4 border border-gray-300 rounded-md ${
          isLoading ? "hidden" : ""
        }`}
      >
        <div>
          <div className="profile"></div>
          <div className="detail">
            <div className="name">{obj.name}</div>
            <div className="remark"></div>
            <div className="proof"></div>
          </div>
          <div className="feedback"></div>
        </div>
        {/* );
        })} */}
      </div>
    </div>
  );
}

export default CDMDetail;
